	<script>
	function request(query){
		var xmlhttp=new XMLHttpRequest();
		xmlhttp.onreadystatechange=function() {
			if (xmlhttp.readyState==4 && xmlhttp.status==200) {
				document.getElementById("request_name").innerHTML=xmlhttp.responseText;
			}
		}
		xmlhttp.open("GET","gethint.php?q="+query,true);
		xmlhttp.send();
	}	
	function showAndHide(a) {
		var li = document.getElementById("L" + a);
		var subMenu = li.getElementsByTagName("ul")[0];
		if (subMenu.style.display == "block") {
			subMenu.style.display = "none";
		} else if (subMenu.style.display == "none") {
			subMenu.style.display = "block";
		}
		var symbol = document.getElementById(a).innerHTML;
		request(symbol);
	}
	function displaySubMenu(li) {
		var subMenu = li.getElementsByTagName("ul")[0];
		subMenu.style.display = "block";
	}
	function hideSubMenu(li) {
		var subMenu = li.getElementsByTagName("ul")[0];
		subMenu.style.display = "none";
	}
